package Lec5;

public class Char_Type {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
//		int i = 243;
//		System.out.println((char) (i));
		System.out.println((int) (ch));
		System.out.println(ch++);// ch = (char)(ch+1)
		System.out.println(ch);
		ch += 1;// ch = (char)(ch+1)
		ch = (char) (ch + 1);
		System.out.println(ch);

	}

}
